// build a list of 140 random words from a given string.
List<String> randomWords(String contents) {
  List<String> allwords = contents.split(' ');
  List<String> result = [];
  var random = new Random();
  for (var i = 0; i < 140; i++) {
    result.add(allwords[random.nextInt(allwords.length)]);
  }
  return result;
}
